INSERT INTO `wp_postmeta` VALUES (390,77,'sss_1_sss_cont','2022 yılının en güncel ürünleri 450 Wp ve 550 Wp olarak satın alınabilir ve yaklaşık 2 veya 2,5 metrekare alan kaplar. Ne kadar elektrik üretiri hesaplarken beş katı olarak düşünebilirsiniz. Örneğin, uygun şartlar sağlanırsa yıllık ortalama 550Wp güneş paneli 2,5 kWh elektrik üretebilir. ');
INSERT INTO `wp_postmeta` VALUES (391,77,'_sss_1_sss_cont','field_64e495a208e1f');
