INSERT INTO `wp_postmeta` VALUES (534,78,'sss_3_sss_cont','Kurulacak olan çift yönlü sayaç üretim ve tüketim değerlerinizi kaydeder. Bu sayaç ile dağıtım şirketi değerlerinizi inceler. Sistem dahilinde cep telefonu uygulamasından veya bilgisayarınızdan anlık üretim değerlerini inceleyebilirsiniz. ');
INSERT INTO `wp_postmeta` VALUES (535,78,'_sss_3_sss_cont','field_64e495a208e1f');
