INSERT INTO `wp_postmeta` VALUES (553,78,'_sss_8_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (554,78,'sss_8_sss_cont','Evet. Şebekeye bağlı on-grid sistemler şebekeye uygun hareket eder.');
INSERT INTO `wp_postmeta` VALUES (555,78,'_sss_8_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (556,78,'sss_9_sss_tit','Elektrik kesintisinde güneş panelleri çalışır mı?');
INSERT INTO `wp_postmeta` VALUES (557,78,'_sss_9_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (558,78,'sss_9_sss_cont','Güneş paneli sisteminiz şebekeye bağlı ise elektrik kesintisi durumunda kapanır. Bu, acil durum müdahale ekiplerinin ve elektrik kurumu tamircilerinin, şebekeye geri güç gönderen panelleriniz nedeniyle yaralanmasını önlemek içindir. Ancak, bir pille eşleştirildiğinde elektrik kesintisinde yedek güç sağlayan, satın alabileceğiniz bazı invertörler vardır.');
INSERT INTO `wp_postmeta` VALUES (559,78,'_sss_9_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (560,78,'sss_10_sss_tit','Sözleşme gücü nedir? ');
INSERT INTO `wp_postmeta` VALUES (561,78,'_sss_10_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (562,78,'sss_10_sss_cont','Bir kullanım yerinin elektrik projesinde belirtilen kurulu gücün kullanım faktörü ile çarpılmış halini ifade eder. Sözleşme gücü, meskende bulunan elektrikli aletleri çalıştırabilmesi için bu aletlerin toplam gücüne göre belirlenir. Kısacası meskenin ihtiyacı olan ve kaldırabileceği güce göre hesaplanır. ');
INSERT INTO `wp_postmeta` VALUES (563,78,'_sss_10_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (564,78,'sss_11_sss_tit','Sistem bakıma ihtiyaç duyar mı?');
