INSERT INTO `wp_postmeta` VALUES (566,78,'sss_11_sss_cont','Bakım ihtiyacı çok azdır. En önemli bakım, dönem dönem panellerin temizlenmesidir. Normalde panel temizliği saf su; iyonize edilmiş su ile yapılır. Şebeke suyu ile yıkanması önerilmez. Sistemin düzgün çalışıp çalışmadığı ise uzaktan izleme sistemi ile takip edilir. ');
INSERT INTO `wp_postmeta` VALUES (567,78,'_sss_11_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (568,78,'sss_12_sss_tit','Güneş paneli bakım maliyeti ne kadardır?');
INSERT INTO `wp_postmeta` VALUES (569,78,'_sss_12_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (570,78,'sss_12_sss_cont','Güneş paneli sistemleri, dayanıklı temperli camdan yapılır ve güç üretecekleri 25 ila 35 yıl boyunca çok az bakım gerektirir. ');
INSERT INTO `wp_postmeta` VALUES (571,78,'_sss_12_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (572,78,'sss_13_sss_tit','Bir sistemi iki ev paylaşabilir mi? ');
INSERT INTO `wp_postmeta` VALUES (573,78,'_sss_13_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (574,78,'sss_13_sss_cont','On-grid sistemde her başvuru için bir elektrik sayacı aboneliği istenir. Sayaç aboneliği hangi eve aitse o ev GES kullanabilir. ');
INSERT INTO `wp_postmeta` VALUES (575,78,'_sss_13_sss_cont','field_64e495a208e1f');
