INSERT INTO `wp_postmeta` VALUES (577,78,'_sss_14_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (578,78,'sss_14_sss_cont','Elektrik aboneliği olan, çatısı gerekli teknik şartlara uygun ve imarlı olan her ev veya iş yeri için kurulabilir. ');
INSERT INTO `wp_postmeta` VALUES (579,78,'_sss_14_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (580,78,'sss_15_sss_tit','Evimin bahçesine GES kurulabilir mi? ');
