INSERT INTO `wp_postmeta` VALUES (1245,182,'sss_0_sss_cont','Evirici, güneş panellerinin ürettiği doğru akım (DC) elektriği elektrikli cihazlarınızı çalıştırmak için ihtiyaç duyulan 230V (tek fazlı) veya 400V (üç fazlı) standart değerlere çevirerek alternatif akıma (AC) dönüştürür. Panellerden gelen doğru akımı (DC Volt) şehir şebeke voltajına uygun olarak alternatif akıma (AC Volt) çeviren cihazdır. ');
INSERT INTO `wp_postmeta` VALUES (1246,182,'_sss_0_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1247,182,'sss_1_sss_tit','Bir adet güneş paneli ne kadar elektrik üretir?');
