INSERT INTO `wp_postmeta` VALUES (1266,182,'_sss_5_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1267,182,'sss_5_sss_cont','Meskenlerde 5-6 yıldır. ');
INSERT INTO `wp_postmeta` VALUES (1268,182,'_sss_5_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1269,182,'sss_6_sss_tit','Sistemin kaç sene ömrü vardır? ');
INSERT INTO `wp_postmeta` VALUES (1270,182,'_sss_6_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1271,182,'sss_6_sss_cont','Sistem tak unut sistemidir. 25 yılın sonunda yaklaşık %80’in üzerinde üretime devam eder.');
INSERT INTO `wp_postmeta` VALUES (1272,182,'_sss_6_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1273,182,'sss_7_sss_tit','Sistem kapalı havalarda elektrik üretir mi? ');
INSERT INTO `wp_postmeta` VALUES (1274,182,'_sss_7_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1275,182,'sss_7_sss_cont','Gündüz güneş olmadığı zamanlarda çok düşük verimle de olsa elektrik üretmeye devam eder. ');
INSERT INTO `wp_postmeta` VALUES (1276,182,'_sss_7_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1277,182,'sss_8_sss_tit','Bölgede elektrik kesilirse GES kurulu evde de elektrik kesilir mi? ');
INSERT INTO `wp_postmeta` VALUES (1278,182,'_sss_8_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1279,182,'sss_8_sss_cont','Evet. Şebekeye bağlı on-grid sistemler şebekeye uygun hareket eder.');
INSERT INTO `wp_postmeta` VALUES (1280,182,'_sss_8_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1281,182,'sss_9_sss_tit','Elektrik kesintisinde güneş panelleri çalışır mı?');
INSERT INTO `wp_postmeta` VALUES (1282,182,'_sss_9_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1283,182,'sss_9_sss_cont','Güneş paneli sisteminiz şebekeye bağlı ise elektrik kesintisi durumunda kapanır. Bu, acil durum müdahale ekiplerinin ve elektrik kurumu tamircilerinin, şebekeye geri güç gönderen panelleriniz nedeniyle yaralanmasını önlemek içindir. Ancak, bir pille eşleştirildiğinde elektrik kesintisinde yedek güç sağlayan, satın alabileceğiniz bazı invertörler vardır.');
INSERT INTO `wp_postmeta` VALUES (1284,182,'_sss_9_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1285,182,'sss_10_sss_tit','Sözleşme gücü nedir? ');
INSERT INTO `wp_postmeta` VALUES (1286,182,'_sss_10_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1287,182,'sss_10_sss_cont','Bir kullanım yerinin elektrik projesinde belirtilen kurulu gücün kullanım faktörü ile çarpılmış halini ifade eder. Sözleşme gücü, meskende bulunan elektrikli aletleri çalıştırabilmesi için bu aletlerin toplam gücüne göre belirlenir. Kısacası meskenin ihtiyacı olan ve kaldırabileceği güce göre hesaplanır. ');
INSERT INTO `wp_postmeta` VALUES (1288,182,'_sss_10_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1289,182,'sss_11_sss_tit','Sistem bakıma ihtiyaç duyar mı?');
INSERT INTO `wp_postmeta` VALUES (1290,182,'_sss_11_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1291,182,'sss_11_sss_cont','Bakım ihtiyacı çok azdır. En önemli bakım, dönem dönem panellerin temizlenmesidir. Normalde panel temizliği saf su; iyonize edilmiş su ile yapılır. Şebeke suyu ile yıkanması önerilmez. Sistemin düzgün çalışıp çalışmadığı ise uzaktan izleme sistemi ile takip edilir. ');
INSERT INTO `wp_postmeta` VALUES (1292,182,'_sss_11_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1293,182,'sss_12_sss_tit','Güneş paneli bakım maliyeti ne kadardır?');
INSERT INTO `wp_postmeta` VALUES (1294,182,'_sss_12_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1295,182,'sss_12_sss_cont','Güneş paneli sistemleri, dayanıklı temperli camdan yapılır ve güç üretecekleri 25 ila 35 yıl boyunca çok az bakım gerektirir. ');
INSERT INTO `wp_postmeta` VALUES (1296,182,'_sss_12_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1297,182,'sss_13_sss_tit','Bir sistemi iki ev paylaşabilir mi? ');
INSERT INTO `wp_postmeta` VALUES (1298,182,'_sss_13_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1299,182,'sss_13_sss_cont','On-grid sistemde her başvuru için bir elektrik sayacı aboneliği istenir. Sayaç aboneliği hangi eve aitse o ev GES kullanabilir. ');
INSERT INTO `wp_postmeta` VALUES (1300,182,'_sss_13_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1301,182,'sss_14_sss_tit','GES nerelere kurulabilir? ');
INSERT INTO `wp_postmeta` VALUES (1302,182,'_sss_14_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1303,182,'sss_14_sss_cont','Elektrik aboneliği olan, çatısı gerekli teknik şartlara uygun ve imarlı olan her ev veya iş yeri için kurulabilir. ');
INSERT INTO `wp_postmeta` VALUES (1304,182,'_sss_14_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1305,182,'sss_15_sss_tit','Evimin bahçesine GES kurulabilir mi? ');
INSERT INTO `wp_postmeta` VALUES (1306,182,'_sss_15_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1307,182,'sss_15_sss_cont','Şu anki mevzuat gereği sadece çatı ve cephelere sistem kurulabilir. ');
INSERT INTO `wp_postmeta` VALUES (1308,182,'_sss_15_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1309,182,'sss_16_sss_tit','Evimin bahçesindeki kapalı otopark çatısına sistem kurulabilir mi? ');
INSERT INTO `wp_postmeta` VALUES (1310,182,'_sss_16_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1311,182,'sss_16_sss_cont','İskan durumunda otopark çatısı gözüküyorsa kurulabilir. ');
INSERT INTO `wp_postmeta` VALUES (1312,182,'_sss_16_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1313,182,'sss_17_sss_tit','Mahsuplaşma nasıl yapılır? ');
INSERT INTO `wp_postmeta` VALUES (1314,182,'_sss_17_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1315,182,'sss_17_sss_cont','Sistem ile çift yönlü sayaç yerleştirilir. Bu sayaç ürettiğiniz ve tükettiğiniz elektrik miktarını ölçer. Aylık olarak dağıtım şirketi bu üretim / tüketim değerlerinizi kaydeder. Üretiminiz tüketiminizden fazla ise üretim miktarı kWh bazında tüketim değerinden düşülür, kalan miktar için dağıtım şirketi hesabınıza fazla üretim ücreti için ödeme yapar. Tüketiminiz üretiminizden fazla ise tüketim miktarı kWh bazında üretim değerinden düşülür, kalan miktar için dağıtım şirketi ödemeniz için fark faturası keser.');
INSERT INTO `wp_postmeta` VALUES (1316,182,'_sss_17_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1317,182,'sss_18_sss_tit','En fazla kaç kw sistem kurulabilir? ');
INSERT INTO `wp_postmeta` VALUES (1318,182,'_sss_18_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1319,182,'sss_18_sss_cont','Sözleşme gücünüz kadar sistem kurabilirsiniz. Sözleşeme gücü elektrik faturanız üzerinden hesaplanır. ');
INSERT INTO `wp_postmeta` VALUES (1320,182,'_sss_18_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1321,182,'sss_19_sss_tit','Fazla elektrik üretildiğinde nasıl ve hangi ücretten satılır? ');
INSERT INTO `wp_postmeta` VALUES (1322,182,'_sss_19_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1323,182,'sss_19_sss_cont','Aylık mahsuplaşma sonrası üretiminiz tüketiminizden fazla ise üretim miktarı kWh bazında tüketim değerinden düşülür. Kalan miktar için dağıtım şirketi hesabınıza fazla üretim ücreti için size verdiği tarife fiyatı üzerinden ödeme yapar. ');
INSERT INTO `wp_postmeta` VALUES (1324,182,'_sss_19_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1325,182,'sss_20_sss_tit','Güneş enerjisinin finansal faydaları nelerdir?');
INSERT INTO `wp_postmeta` VALUES (1326,182,'_sss_20_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1327,182,'sss_20_sss_cont','Evinize güneş enerjisi sistemi kurduğunuzda, elektrik faturalarınızdan tasarruf eder ve gelecekte artan elektrik fiyatlarına karşı kendinizi güvence altına alırsınız. Ne kadar tasarruf edebileceğiniz, bölgenizdeki kullanım oranlarına ve güneş enerjisi politikalarına bağlıdır, ancak nerede yaşarsanız yaşayın güneş enerjisine geçmek akıllı bir yatırımdır.');
INSERT INTO `wp_postmeta` VALUES (1328,182,'_sss_20_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1329,182,'sss_21_sss_tit','Güneş enerjisinin çevresel faydaları nelerdir?');
INSERT INTO `wp_postmeta` VALUES (1330,182,'_sss_21_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1331,182,'sss_21_sss_cont','Diğer yenilenebilir enerji kaynakları gibi güneş enerjisinin de çevresel ve sağlık açısından birçok faydası vardır. Güneş enerjisine geçtiğinizde, iklim değişikliğine sebep olan sera gazı emisyonlarının azalmasına katkı sağlar, sağlık sorunlarına neden olabilecek kükürt dioksit ve partikül madde gibi daha az hava kirleticisi ile enerjinize kavuşursunuz. ');
INSERT INTO `wp_postmeta` VALUES (1332,182,'_sss_21_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1333,182,'sss_22_sss_tit','Elektrik için ne kadar ödediğimi nasıl öğrenebilirim?');
INSERT INTO `wp_postmeta` VALUES (1334,182,'_sss_22_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1335,182,'sss_22_sss_cont','Elektrik için ne kadar ödediğinizi (ve ayda ne kadar elektrik kullandığınızı) öğrenmenin en kolay yolu, elektrik faturanıza bakmaktır.');
INSERT INTO `wp_postmeta` VALUES (1336,182,'_sss_22_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1337,182,'sss_23_sss_tit','Net ölçüm nedir?');
INSERT INTO `wp_postmeta` VALUES (1338,182,'_sss_23_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1339,182,'sss_23_sss_cont','Net ölçüm, kamu hizmetlerinin güneş enerjisi sistemi sahiplerine güneş panelleri tarafından üretilen elektrik için kredi vermek için kullandıkları sistemdir. Net ölçüm ile, yalnızca güneş panellerinizin üretebileceğinin ötesinde kullandığınız elektrik için ödeme yaparsınız. ');
INSERT INTO `wp_postmeta` VALUES (1340,182,'_sss_23_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1341,182,'sss_24_sss_tit','Güneş enerjisi evin değerlerini nasıl etkiler?');
INSERT INTO `wp_postmeta` VALUES (1342,182,'_sss_24_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1343,182,'sss_24_sss_cont','Araştırmalar, güneş enerjisi sistemleri olan evlerin, güneş enerjisi sistemleri olmayan evlerden daha fazla sattığını gösteriyor. Ülkenin çoğu yerinde, güneş ışığına geçmek aslında evinizin değerini bir mutfak yenilemesinden daha fazla artırır.');
INSERT INTO `wp_postmeta` VALUES (1344,182,'_sss_24_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1345,182,'sss_25_sss_tit','Güneş panellerine kar yağarsa ne olur?');
INSERT INTO `wp_postmeta` VALUES (1346,182,'_sss_25_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1347,182,'sss_25_sss_cont','Güneş panelleri güneş ışığını güce dönüştürür, bu nedenle panelleriniz karla kaplıysa elektrik üretemezler. Kar, genellikle panellerinizde yapısal sorunlara yol açacak kadar ağır değildir ve çoğu panel belirli bir açıyla eğildiğinden, kar kayar. Kar biriktiğinde panellerinizin temizlenmesi kolaydır.');
INSERT INTO `wp_postmeta` VALUES (1348,182,'_sss_25_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1349,182,'sss_26_sss_tit','Güneş panelleri ile şebekeden çıkabilir miyim?');
INSERT INTO `wp_postmeta` VALUES (1350,182,'_sss_26_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1351,182,'sss_26_sss_cont','Mülkünüze güneş panelleri kurduğunuzda, hala şebekeye bağlı olacaksınız. Bu, sisteminiz ihtiyacınız olan gücün tamamını üretmediğinde şebekeden çekmenizi ve kullandığınızdan fazlasını ürettiğinizde şebekeye geri göndermenizi sağlar.');
INSERT INTO `wp_postmeta` VALUES (1352,182,'_sss_26_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1353,182,'sss_27_sss_tit','Güneş paneli sistemimi satın almalı mıyım yoksa kiralamalı mıyım?');
INSERT INTO `wp_postmeta` VALUES (1354,182,'_sss_27_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1355,182,'sss_27_sss_cont','Güneş enerjisi sisteminizi satın alma veya kiralama kararı, güneş enerjisi kullanma nedenlerinize bağlıdır. Güneş enerjisi sisteminizin finansal getirilerini en üst düzeye çıkarmak istiyorsanız sistemi satın almak sizin için daha iyi bir karardır. Bununla birlikte, enerji faturalarınızı azaltmak ve çevreyi korumak için kolay ve bakım gerektirmeyen bir yola öncelik veriyorsanız, güneş enerjisi kiralamayı düşünmelisiniz.');
INSERT INTO `wp_postmeta` VALUES (1356,182,'_sss_27_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1357,182,'sss_28_sss_tit','Güneş enerjisine geçebilir miyim?');
INSERT INTO `wp_postmeta` VALUES (1358,182,'_sss_28_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1359,182,'sss_28_sss_cont','Elektrik faturanızı ödeyebiliyorsanız, güneş enerjisi kullanabilirsiniz. Hem güneş enerjisi kredileri hem de güneş enerjisi kiralamaları dahil 0 $\'lık indirimli güneş enerjisi finansmanı seçenekleri, kredisi iyi olan ev sahiplerinin güneş enerjisine geçerek elektrik faturalarında tasarruf etmeye başlamalarını kolaylaştırır.');
INSERT INTO `wp_postmeta` VALUES (1360,182,'_sss_28_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1361,182,'sss_29_sss_tit','Çatım güneş panelleri için uygun mu?');
INSERT INTO `wp_postmeta` VALUES (1362,182,'_sss_29_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1363,182,'sss_29_sss_cont','Gölgesi çok az olan veya hiç olmayan ve bir güneş paneli sistemini sığdırmak için yeterli alana sahip güneye bakan çatılar, güneş enerjisi kurmak için idealdir. Ancak çoğu durumda geçici çözümler vardır; tüm kurulum teklifleri, gerçek çatınızın resimlerine dayanır.');
INSERT INTO `wp_postmeta` VALUES (1364,182,'_sss_29_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1365,182,'sss_30_sss_tit','Ne büyüklükte güneş enerjisi sistemi almalıyım?');
INSERT INTO `wp_postmeta` VALUES (1366,182,'_sss_30_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1367,182,'sss_30_sss_cont','Güneş enerjisi sisteminizin büyüklüğü, yaşadığınız yerdeki hava koşullarının yanı sıra aylık olarak ne kadar elektrik kullandığınıza da bağlıdır. Size doğru bir teklif verebilmemiz için 12 aylık enerji tüketim geçmişinize bakmamız gerekir.');
INSERT INTO `wp_postmeta` VALUES (1368,182,'_sss_30_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1369,182,'sss_31_sss_tit','Güneş enerjisini kurmadan önce çatımı değiştirmem gerekir mi?');
INSERT INTO `wp_postmeta` VALUES (1370,182,'_sss_31_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1371,182,'sss_31_sss_cont','Çatınızı değiştirmeniz gerekirse söküp tekrar takmak maliyetli olabilir. Çatınızın yakın zamanda bakıma ihtiyacı varsa güneş enerjisi tesisatınızı bitirmeden bakımını tamamlamalısınız.');
INSERT INTO `wp_postmeta` VALUES (1372,182,'_sss_31_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1373,182,'sss_32_sss_tit','Güneş enerjisi sistemi ne kadar dayanır?');
INSERT INTO `wp_postmeta` VALUES (1374,182,'_sss_32_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1375,182,'sss_32_sss_cont','Genel olarak, güneş panelleri çok dayanıklıdır. Kar, rüzgar ve doludan etkilenmezler. Güneş enerjisi sisteminizin çeşitli bileşenlerinin farklı zamanlarda değiştirilmesi gerekir. Ancak sisteminiz 25 ila 35 yıl elektrik üretmeye devam eder.');
INSERT INTO `wp_postmeta` VALUES (1376,182,'_sss_32_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1377,182,'det_img','');
INSERT INTO `wp_postmeta` VALUES (1378,182,'_det_img','field_64e9e76f07b50');
INSERT INTO `wp_postmeta` VALUES (1379,183,'sss_0_sss_tit','Evirici nedir? İnvertör? İnverter? ');
INSERT INTO `wp_postmeta` VALUES (1380,183,'_sss_0_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1381,183,'sss_0_sss_cont','Evirici, güneş panellerinin ürettiği doğru akım (DC) elektriği elektrikli cihazlarınızı çalıştırmak için ihtiyaç duyulan 230V (tek fazlı) veya 400V (üç fazlı) standart değerlere çevirerek alternatif akıma (AC) dönüştürür. Panellerden gelen doğru akımı (DC Volt) şehir şebeke voltajına uygun olarak alternatif akıma (AC Volt) çeviren cihazdır. ');
INSERT INTO `wp_postmeta` VALUES (1382,183,'_sss_0_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1383,183,'sss_1_sss_tit','Bir adet güneş paneli ne kadar elektrik üretir?');
INSERT INTO `wp_postmeta` VALUES (1384,183,'_sss_1_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1385,183,'sss_1_sss_cont','2022 yılının en güncel ürünleri 450 Wp ve 550 Wp olarak satın alınabilir ve yaklaşık 2 veya 2,5 metrekare alan kaplar. Ne kadar elektrik üretiri hesaplarken beş katı olarak düşünebilirsiniz. Örneğin, uygun şartlar sağlanırsa yıllık ortalama 550Wp güneş paneli 2,5 kWh elektrik üretebilir. ');
INSERT INTO `wp_postmeta` VALUES (1386,183,'_sss_1_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1387,183,'sss_2_sss_tit','Sistem kurulunca elektrik faturası ödemeye devam eder miyim? ');
INSERT INTO `wp_postmeta` VALUES (1388,183,'_sss_2_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1389,183,'sss_2_sss_cont','Kurulan sistem mevcut kullanılan gücün altında olmaması durumunda elektrik faturası ödemezsiniz. ');
INSERT INTO `wp_postmeta` VALUES (1390,183,'_sss_2_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1391,183,'sss','33');
INSERT INTO `wp_postmeta` VALUES (1392,183,'_sss','field_64e4958108e1d');
INSERT INTO `wp_postmeta` VALUES (1393,183,'sss_3_sss_tit','Enerji üretim ve tüketim değerleri nasıl takip edilir? ');
INSERT INTO `wp_postmeta` VALUES (1394,183,'_sss_3_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1395,183,'sss_3_sss_cont','Kurulacak olan çift yönlü sayaç üretim ve tüketim değerlerinizi kaydeder. Bu sayaç ile dağıtım şirketi değerlerinizi inceler. Sistem dahilinde cep telefonu uygulamasından veya bilgisayarınızdan anlık üretim değerlerini inceleyebilirsiniz. ');
INSERT INTO `wp_postmeta` VALUES (1396,183,'_sss_3_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1397,183,'sss_4_sss_tit','Sistem kurulum süresi ne kadardır? ');
INSERT INTO `wp_postmeta` VALUES (1398,183,'_sss_4_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1399,183,'sss_4_sss_cont','Teknik olarak sadece sistem kurulumu ortalama 3 gündür. Ancak evrak ve bürokrasi işleri ile toplam 2-3 ay sürer. ');
INSERT INTO `wp_postmeta` VALUES (1400,183,'_sss_4_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1401,183,'sss_5_sss_tit','Sistem amortisman süresi ne kadardır? ');
INSERT INTO `wp_postmeta` VALUES (1402,183,'_sss_5_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1403,183,'sss_5_sss_cont','Meskenlerde 5-6 yıldır. ');
INSERT INTO `wp_postmeta` VALUES (1404,183,'_sss_5_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1405,183,'sss_6_sss_tit','Sistemin kaç sene ömrü vardır? ');
INSERT INTO `wp_postmeta` VALUES (1406,183,'_sss_6_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1407,183,'sss_6_sss_cont','Sistem tak unut sistemidir. 25 yılın sonunda yaklaşık %80’in üzerinde üretime devam eder.');
INSERT INTO `wp_postmeta` VALUES (1408,183,'_sss_6_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1409,183,'sss_7_sss_tit','Sistem kapalı havalarda elektrik üretir mi? ');
INSERT INTO `wp_postmeta` VALUES (1410,183,'_sss_7_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1411,183,'sss_7_sss_cont','Gündüz güneş olmadığı zamanlarda çok düşük verimle de olsa elektrik üretmeye devam eder. ');
INSERT INTO `wp_postmeta` VALUES (1412,183,'_sss_7_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1413,183,'sss_8_sss_tit','Bölgede elektrik kesilirse GES kurulu evde de elektrik kesilir mi? ');
INSERT INTO `wp_postmeta` VALUES (1414,183,'_sss_8_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1415,183,'sss_8_sss_cont','Evet. Şebekeye bağlı on-grid sistemler şebekeye uygun hareket eder.');
INSERT INTO `wp_postmeta` VALUES (1416,183,'_sss_8_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1417,183,'sss_9_sss_tit','Elektrik kesintisinde güneş panelleri çalışır mı?');
INSERT INTO `wp_postmeta` VALUES (1418,183,'_sss_9_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1419,183,'sss_9_sss_cont','Güneş paneli sisteminiz şebekeye bağlı ise elektrik kesintisi durumunda kapanır. Bu, acil durum müdahale ekiplerinin ve elektrik kurumu tamircilerinin, şebekeye geri güç gönderen panelleriniz nedeniyle yaralanmasını önlemek içindir. Ancak, bir pille eşleştirildiğinde elektrik kesintisinde yedek güç sağlayan, satın alabileceğiniz bazı invertörler vardır.');
INSERT INTO `wp_postmeta` VALUES (1420,183,'_sss_9_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1421,183,'sss_10_sss_tit','Sözleşme gücü nedir? ');
INSERT INTO `wp_postmeta` VALUES (1422,183,'_sss_10_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1423,183,'sss_10_sss_cont','Bir kullanım yerinin elektrik projesinde belirtilen kurulu gücün kullanım faktörü ile çarpılmış halini ifade eder. Sözleşme gücü, meskende bulunan elektrikli aletleri çalıştırabilmesi için bu aletlerin toplam gücüne göre belirlenir. Kısacası meskenin ihtiyacı olan ve kaldırabileceği güce göre hesaplanır. ');
INSERT INTO `wp_postmeta` VALUES (1424,183,'_sss_10_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1425,183,'sss_11_sss_tit','Sistem bakıma ihtiyaç duyar mı?');
INSERT INTO `wp_postmeta` VALUES (1426,183,'_sss_11_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1427,183,'sss_11_sss_cont','Bakım ihtiyacı çok azdır. En önemli bakım, dönem dönem panellerin temizlenmesidir. Normalde panel temizliği saf su; iyonize edilmiş su ile yapılır. Şebeke suyu ile yıkanması önerilmez. Sistemin düzgün çalışıp çalışmadığı ise uzaktan izleme sistemi ile takip edilir. ');
INSERT INTO `wp_postmeta` VALUES (1428,183,'_sss_11_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1429,183,'sss_12_sss_tit','Güneş paneli bakım maliyeti ne kadardır?');
INSERT INTO `wp_postmeta` VALUES (1430,183,'_sss_12_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1431,183,'sss_12_sss_cont','Güneş paneli sistemleri, dayanıklı temperli camdan yapılır ve güç üretecekleri 25 ila 35 yıl boyunca çok az bakım gerektirir. ');
INSERT INTO `wp_postmeta` VALUES (1432,183,'_sss_12_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1433,183,'sss_13_sss_tit','Bir sistemi iki ev paylaşabilir mi? ');
INSERT INTO `wp_postmeta` VALUES (1434,183,'_sss_13_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1435,183,'sss_13_sss_cont','On-grid sistemde her başvuru için bir elektrik sayacı aboneliği istenir. Sayaç aboneliği hangi eve aitse o ev GES kullanabilir. ');
INSERT INTO `wp_postmeta` VALUES (1436,183,'_sss_13_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1437,183,'sss_14_sss_tit','GES nerelere kurulabilir? ');
INSERT INTO `wp_postmeta` VALUES (1438,183,'_sss_14_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1439,183,'sss_14_sss_cont','Elektrik aboneliği olan, çatısı gerekli teknik şartlara uygun ve imarlı olan her ev veya iş yeri için kurulabilir. ');
INSERT INTO `wp_postmeta` VALUES (1440,183,'_sss_14_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1441,183,'sss_15_sss_tit','Evimin bahçesine GES kurulabilir mi? ');
INSERT INTO `wp_postmeta` VALUES (1442,183,'_sss_15_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1443,183,'sss_15_sss_cont','Şu anki mevzuat gereği sadece çatı ve cephelere sistem kurulabilir. ');
INSERT INTO `wp_postmeta` VALUES (1444,183,'_sss_15_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1445,183,'sss_16_sss_tit','Evimin bahçesindeki kapalı otopark çatısına sistem kurulabilir mi? ');
INSERT INTO `wp_postmeta` VALUES (1446,183,'_sss_16_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1447,183,'sss_16_sss_cont','İskan durumunda otopark çatısı gözüküyorsa kurulabilir. ');
INSERT INTO `wp_postmeta` VALUES (1448,183,'_sss_16_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1449,183,'sss_17_sss_tit','Mahsuplaşma nasıl yapılır? ');
INSERT INTO `wp_postmeta` VALUES (1450,183,'_sss_17_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1451,183,'sss_17_sss_cont','Sistem ile çift yönlü sayaç yerleştirilir. Bu sayaç ürettiğiniz ve tükettiğiniz elektrik miktarını ölçer. Aylık olarak dağıtım şirketi bu üretim / tüketim değerlerinizi kaydeder. Üretiminiz tüketiminizden fazla ise üretim miktarı kWh bazında tüketim değerinden düşülür, kalan miktar için dağıtım şirketi hesabınıza fazla üretim ücreti için ödeme yapar. Tüketiminiz üretiminizden fazla ise tüketim miktarı kWh bazında üretim değerinden düşülür, kalan miktar için dağıtım şirketi ödemeniz için fark faturası keser.');
INSERT INTO `wp_postmeta` VALUES (1452,183,'_sss_17_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1453,183,'sss_18_sss_tit','En fazla kaç kw sistem kurulabilir? ');
INSERT INTO `wp_postmeta` VALUES (1454,183,'_sss_18_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1455,183,'sss_18_sss_cont','Sözleşme gücünüz kadar sistem kurabilirsiniz. Sözleşeme gücü elektrik faturanız üzerinden hesaplanır. ');
INSERT INTO `wp_postmeta` VALUES (1456,183,'_sss_18_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1457,183,'sss_19_sss_tit','Fazla elektrik üretildiğinde nasıl ve hangi ücretten satılır? ');
INSERT INTO `wp_postmeta` VALUES (1458,183,'_sss_19_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1459,183,'sss_19_sss_cont','Aylık mahsuplaşma sonrası üretiminiz tüketiminizden fazla ise üretim miktarı kWh bazında tüketim değerinden düşülür. Kalan miktar için dağıtım şirketi hesabınıza fazla üretim ücreti için size verdiği tarife fiyatı üzerinden ödeme yapar. ');
INSERT INTO `wp_postmeta` VALUES (1460,183,'_sss_19_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1461,183,'sss_20_sss_tit','Güneş enerjisinin finansal faydaları nelerdir?');
INSERT INTO `wp_postmeta` VALUES (1462,183,'_sss_20_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1463,183,'sss_20_sss_cont','Evinize güneş enerjisi sistemi kurduğunuzda, elektrik faturalarınızdan tasarruf eder ve gelecekte artan elektrik fiyatlarına karşı kendinizi güvence altına alırsınız. Ne kadar tasarruf edebileceğiniz, bölgenizdeki kullanım oranlarına ve güneş enerjisi politikalarına bağlıdır, ancak nerede yaşarsanız yaşayın güneş enerjisine geçmek akıllı bir yatırımdır.');
INSERT INTO `wp_postmeta` VALUES (1464,183,'_sss_20_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1465,183,'sss_21_sss_tit','Güneş enerjisinin çevresel faydaları nelerdir?');
INSERT INTO `wp_postmeta` VALUES (1466,183,'_sss_21_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1467,183,'sss_21_sss_cont','Diğer yenilenebilir enerji kaynakları gibi güneş enerjisinin de çevresel ve sağlık açısından birçok faydası vardır. Güneş enerjisine geçtiğinizde, iklim değişikliğine sebep olan sera gazı emisyonlarının azalmasına katkı sağlar, sağlık sorunlarına neden olabilecek kükürt dioksit ve partikül madde gibi daha az hava kirleticisi ile enerjinize kavuşursunuz. ');
INSERT INTO `wp_postmeta` VALUES (1468,183,'_sss_21_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1469,183,'sss_22_sss_tit','Elektrik için ne kadar ödediğimi nasıl öğrenebilirim?');
INSERT INTO `wp_postmeta` VALUES (1470,183,'_sss_22_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1471,183,'sss_22_sss_cont','Elektrik için ne kadar ödediğinizi (ve ayda ne kadar elektrik kullandığınızı) öğrenmenin en kolay yolu, elektrik faturanıza bakmaktır.');
INSERT INTO `wp_postmeta` VALUES (1472,183,'_sss_22_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1473,183,'sss_23_sss_tit','Net ölçüm nedir?');
INSERT INTO `wp_postmeta` VALUES (1474,183,'_sss_23_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1475,183,'sss_23_sss_cont','Net ölçüm, kamu hizmetlerinin güneş enerjisi sistemi sahiplerine güneş panelleri tarafından üretilen elektrik için kredi vermek için kullandıkları sistemdir. Net ölçüm ile, yalnızca güneş panellerinizin üretebileceğinin ötesinde kullandığınız elektrik için ödeme yaparsınız. ');
INSERT INTO `wp_postmeta` VALUES (1476,183,'_sss_23_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1477,183,'sss_24_sss_tit','Güneş enerjisi evin değerlerini nasıl etkiler?');
INSERT INTO `wp_postmeta` VALUES (1478,183,'_sss_24_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1479,183,'sss_24_sss_cont','Araştırmalar, güneş enerjisi sistemleri olan evlerin, güneş enerjisi sistemleri olmayan evlerden daha fazla sattığını gösteriyor. Ülkenin çoğu yerinde, güneş ışığına geçmek aslında evinizin değerini bir mutfak yenilemesinden daha fazla artırır.');
INSERT INTO `wp_postmeta` VALUES (1480,183,'_sss_24_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1481,183,'sss_25_sss_tit','Güneş panellerine kar yağarsa ne olur?');
INSERT INTO `wp_postmeta` VALUES (1482,183,'_sss_25_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1483,183,'sss_25_sss_cont','Güneş panelleri güneş ışığını güce dönüştürür, bu nedenle panelleriniz karla kaplıysa elektrik üretemezler. Kar, genellikle panellerinizde yapısal sorunlara yol açacak kadar ağır değildir ve çoğu panel belirli bir açıyla eğildiğinden, kar kayar. Kar biriktiğinde panellerinizin temizlenmesi kolaydır.');
INSERT INTO `wp_postmeta` VALUES (1484,183,'_sss_25_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1485,183,'sss_26_sss_tit','Güneş panelleri ile şebekeden çıkabilir miyim?');
INSERT INTO `wp_postmeta` VALUES (1486,183,'_sss_26_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1487,183,'sss_26_sss_cont','Mülkünüze güneş panelleri kurduğunuzda, hala şebekeye bağlı olacaksınız. Bu, sisteminiz ihtiyacınız olan gücün tamamını üretmediğinde şebekeden çekmenizi ve kullandığınızdan fazlasını ürettiğinizde şebekeye geri göndermenizi sağlar.');
INSERT INTO `wp_postmeta` VALUES (1488,183,'_sss_26_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1489,183,'sss_27_sss_tit','Güneş paneli sistemimi satın almalı mıyım yoksa kiralamalı mıyım?');
INSERT INTO `wp_postmeta` VALUES (1490,183,'_sss_27_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1491,183,'sss_27_sss_cont','Güneş enerjisi sisteminizi satın alma veya kiralama kararı, güneş enerjisi kullanma nedenlerinize bağlıdır. Güneş enerjisi sisteminizin finansal getirilerini en üst düzeye çıkarmak istiyorsanız sistemi satın almak sizin için daha iyi bir karardır. Bununla birlikte, enerji faturalarınızı azaltmak ve çevreyi korumak için kolay ve bakım gerektirmeyen bir yola öncelik veriyorsanız, güneş enerjisi kiralamayı düşünmelisiniz.');
INSERT INTO `wp_postmeta` VALUES (1492,183,'_sss_27_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1493,183,'sss_28_sss_tit','Güneş enerjisine geçebilir miyim?');
INSERT INTO `wp_postmeta` VALUES (1494,183,'_sss_28_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1495,183,'sss_28_sss_cont','Elektrik faturanızı ödeyebiliyorsanız, güneş enerjisi kullanabilirsiniz. Hem güneş enerjisi kredileri hem de güneş enerjisi kiralamaları dahil 0 $\'lık indirimli güneş enerjisi finansmanı seçenekleri, kredisi iyi olan ev sahiplerinin güneş enerjisine geçerek elektrik faturalarında tasarruf etmeye başlamalarını kolaylaştırır.');
INSERT INTO `wp_postmeta` VALUES (1496,183,'_sss_28_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1497,183,'sss_29_sss_tit','Çatım güneş panelleri için uygun mu?');
INSERT INTO `wp_postmeta` VALUES (1498,183,'_sss_29_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1499,183,'sss_29_sss_cont','Gölgesi çok az olan veya hiç olmayan ve bir güneş paneli sistemini sığdırmak için yeterli alana sahip güneye bakan çatılar, güneş enerjisi kurmak için idealdir. Ancak çoğu durumda geçici çözümler vardır; tüm kurulum teklifleri, gerçek çatınızın resimlerine dayanır.');
INSERT INTO `wp_postmeta` VALUES (1500,183,'_sss_29_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1501,183,'sss_30_sss_tit','Ne büyüklükte güneş enerjisi sistemi almalıyım?');
INSERT INTO `wp_postmeta` VALUES (1502,183,'_sss_30_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1503,183,'sss_30_sss_cont','Güneş enerjisi sisteminizin büyüklüğü, yaşadığınız yerdeki hava koşullarının yanı sıra aylık olarak ne kadar elektrik kullandığınıza da bağlıdır. Size doğru bir teklif verebilmemiz için 12 aylık enerji tüketim geçmişinize bakmamız gerekir.');
INSERT INTO `wp_postmeta` VALUES (1504,183,'_sss_30_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1505,183,'sss_31_sss_tit','Güneş enerjisini kurmadan önce çatımı değiştirmem gerekir mi?');
INSERT INTO `wp_postmeta` VALUES (1506,183,'_sss_31_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1507,183,'sss_31_sss_cont','Çatınızı değiştirmeniz gerekirse söküp tekrar takmak maliyetli olabilir. Çatınızın yakın zamanda bakıma ihtiyacı varsa güneş enerjisi tesisatınızı bitirmeden bakımını tamamlamalısınız.');
INSERT INTO `wp_postmeta` VALUES (1508,183,'_sss_31_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1509,183,'sss_32_sss_tit','Güneş enerjisi sistemi ne kadar dayanır?');
INSERT INTO `wp_postmeta` VALUES (1510,183,'_sss_32_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (1511,183,'sss_32_sss_cont','Genel olarak, güneş panelleri çok dayanıklıdır. Kar, rüzgar ve doludan etkilenmezler. Güneş enerjisi sisteminizin çeşitli bileşenlerinin farklı zamanlarda değiştirilmesi gerekir. Ancak sisteminiz 25 ila 35 yıl elektrik üretmeye devam eder.');
INSERT INTO `wp_postmeta` VALUES (1512,183,'_sss_32_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (1513,183,'det_img','');
