INSERT INTO `wp_postmeta` VALUES (2319,241,'sss_6_sss_cont','Bu, çatınızın yapısal dayanıklılığına bağlıdır. Uzman bir inceleme ile belirlenmelidir');
INSERT INTO `wp_postmeta` VALUES (2320,241,'_sss_6_sss_cont','field_64e495a208e1f');
