INSERT INTO `wp_postmeta` VALUES (2322,241,'_sss_7_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2323,241,'sss_7_sss_cont','Çatınızın yakın zamanda bakıma ihtiyacı varsa güneş enerjisi tesisatınızı bitirmeden bakımını tamamlamalısınız.');
INSERT INTO `wp_postmeta` VALUES (2324,241,'_sss_7_sss_cont','field_64e495a208e1f');
