INSERT INTO `wp_postmeta` VALUES (2326,241,'_sss_8_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2327,241,'sss_8_sss_cont','Güneş enerjisi sisteminizin büyüklüğü, yaşadığınız yerdeki hava koşullarının yanı sıra aylık olarak ne kadar elektrik tükettiğinize da bağlıdır. Size doğru bir teklif verebilmemiz için 12 aylık enerji tüketim geçmişinize bakmamız gerekir.');
INSERT INTO `wp_postmeta` VALUES (2328,241,'_sss_8_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (2329,241,'sss_9_sss_tit','Çatıma en fazla kaç kW sistem kurulabilir? ');
INSERT INTO `wp_postmeta` VALUES (2330,241,'_sss_9_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2331,241,'sss_9_sss_cont','Sözleşme gücünüz kadar sistem kurabilirsiniz. Sözleşeme gücü elektrik faturanız üzerinden hesaplanır. ');
INSERT INTO `wp_postmeta` VALUES (2332,241,'_sss_9_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (2333,241,'sss_10_sss_tit','Sözleşme gücü nedir? ');
INSERT INTO `wp_postmeta` VALUES (2334,241,'_sss_10_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2335,241,'sss_10_sss_cont','Sözleşme gücü, meskende bulunan elektrikli aletleri çalıştırabilmesi için bu aletlerin toplam gücüne göre belirlenir. Kısacası meskenin ihtiyacı olan ve kaldırabileceği güce göre hesaplanır. ');
INSERT INTO `wp_postmeta` VALUES (2336,241,'_sss_10_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (2337,241,'sss_11_sss_tit','Sistem kurulunca elektrik faturası ödemeye devam eder miyim? ');
INSERT INTO `wp_postmeta` VALUES (2338,241,'_sss_11_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2339,241,'sss_11_sss_cont','Gündüz güneş olmadığı zamanlarda çok düşük verimle de olsa elektrik üretmeye devam eder. Ayrıca yasaların vermiş olduğu aylık mahsuplaşma hakkı ile az üretim yapılan gün ile fazla üretim yapılan gün mahsuplaştırılabilmektedir. (1 ay içerisinde kaç kwh enerji üretildiği ve tüketildiğine dair değerler önem arz etmektedir.)');
INSERT INTO `wp_postmeta` VALUES (2340,241,'_sss_11_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (2341,241,'sss_12_sss_tit','Bölgede elektrik kesilirse GES kurulu evimde de elektrik kesilir mi? ');
INSERT INTO `wp_postmeta` VALUES (2342,241,'_sss_12_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2343,241,'sss_12_sss_cont','Evet. Şebekeye bağlı on-grid sistemler şebekeye uygun hareket eder.');
INSERT INTO `wp_postmeta` VALUES (2344,241,'_sss_12_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (2345,241,'sss_13_sss_tit','Evimde Güneş Enerjisi Sistemleri kurdurduğumda  şebekeden çıkabilir miyim? ');
INSERT INTO `wp_postmeta` VALUES (2346,241,'_sss_13_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2347,241,'sss_13_sss_cont','Evinize güneş panelleri kurduğunuzda, hala şebekeye bağlı olacaksınız. Bunun temel sebebi; sisteminizin ihtiyacınız olan gücün tamamını üretmediğinde şebekeden enerji çekmenizi ve kullandığınızdan fazlasını ürettiğinizde de şebekeye geri göndermenizi sağlamasıdır. Ancak, bulunduğunuz bölge ve çevresel faktörler nedeniyle elektrik şebekesine erişiminizin olmaması durumunda, isteğe bağlı olarak tamamen şebekeden bağımsız bir sistemin  de kurulması mümkündür.');
INSERT INTO `wp_postmeta` VALUES (2348,241,'_sss_13_sss_cont','field_64e495a208e1f');
INSERT INTO `wp_postmeta` VALUES (2349,241,'sss_14_sss_tit','GES nerelere kurulabilir? ');
