INSERT INTO `wp_postmeta` VALUES (2351,241,'sss_14_sss_cont','Elektrik  şebeke aboneliği olan, çatısı gerekli teknik şartlara uygun ve imarlı olan her  müştakil ev veya iş yeri için kurulabilir. ');
INSERT INTO `wp_postmeta` VALUES (2352,241,'_sss_14_sss_cont','field_64e495a208e1f');
