INSERT INTO `wp_postmeta` VALUES (2494,254,'sss_2_sss_tit','Evim veya işyerim için güneş enerjisi kurulumu yapmanın maliyeti nedir?');
INSERT INTO `wp_postmeta` VALUES (2495,254,'_sss_2_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2496,254,'sss_2_sss_cont','Sistemin maliyeti, tüketilen ve artan enerji ihtiyacına, bölge koşulları, ve donanım kalitesine göre değişiklik gösterir. Uzun ömürlü bir sistem kurmak için   yüksek verimliliği olan donanımlarla profesyonel projelendirme sonrası kurulum yapılması önerilir.');
