INSERT INTO `wp_postmeta` VALUES (2641,255,'_sss_18_sss_tit','field_64e4959a08e1e');
INSERT INTO `wp_postmeta` VALUES (2642,255,'sss_18_sss_cont','Araştırmalar, güneş enerjisi sistemleri olan evlerin, güneş enerjisi sistemleri olmayan evlerden daha fazla satıldığını gösteriyor. Güneş enerjisine geçmek evinizin değerini bir mutfak yenilemesinden daha fazla artırır.');
