INSERT INTO `wp_postmeta` VALUES (4038,421,'equip_content','<section id=\"section1\" class=\"section systems-feed container fadeup\">\r\n                <span>\r\n                    GROWATT INVERTER\r\n                </span>\r\n                <div class=\"code\">\r\n                    SPF3500~5000ES OFF GRID\r\n                </div>\r\n                <div class=\"info\">\r\n                    <div class=\"list bg-none\">\r\n                        <ol>\r\n                            <li>Entegre MPPT şarj kontrol cihazı  </li>\r\n                            <li>Dengeleme şarj fonksiyonu  </li>\r\n                            <li>Maksimum (450VDC\'ye kadar) PV giriş voltajı </li>\r\n                            <li>Yapılandırılabilir şebeke veya güneş enerjisi giriş önceliği. </li>\r\n                            <li>İsteğe bağlı WIFI/GPRS uzaktan izleme. </li>\r\n                            <li>30kW\'a kadar kapasite artırımı için paralel çalışmayı destekler</li>                \r\n                            <li>PV enerjisi yetersizse PV ve Şebeke yüke ortak güç sağlar  </li>\r\n                            <li>İnvertörün şarj ve deşarj süresini esnek bir şekilde programlar </li>\r\n\r\n                        </ol>\r\n                    </div>\r\n\r\n                    <div class=\"image\">\r\n                        <figure>\r\n                            <img src=\"/wp-content/uploads/2023/11/Growatt-SPF-3500-5000-ES-1.png\">\r\n                        </figure>\r\n                    </div>\r\n                </div>\r\n               \r\n            </section>\r\n\r\n          <section id=\"section1\" class=\"section systems-feed container fadeup\">\r\n                <span>\r\n                    GROWATT INVERTER\r\n                </span>\r\n                <div class=\"code\">\r\n                    SPH4000~10000TL3 BH-UP\r\n\r\n                </div>\r\n                <div class=\"info\">\r\n                    <div class=\"list bg-none\">\r\n         