INSERT INTO `wp_posts` VALUES (195,1,'2023-09-01 15:19:07','2023-09-01 12:19:07','IBT Solar Group Kurucusu Hakan Daltaban \"Bugün için bireysel çatı GES pazarı, 10 milyar dolarlık bir hacme ulaşırken tarımsal sulama GES pazarının hacmi ise 18 milyar dolar seviyesinde... Gelecek 7 yıl içinde ortalama 10 kat büyüyeceğini öngörüyoruz\" dedi. Ülkenin güneşten elde edilen kurulu gücüne 300 megavat katkıda bulunduklarını belirten Daltaban, gelecek 3 yıl içinde Avrupa\'da da ilk sıralarda olmayı hedeflediklerini ifade etti.\r\n\r\n<b> Sözcü </b>','Çatıdan elektrik pazarı büyüyor.','','inherit','closed','closed','','66-revision-v1','','','2023-09-01 15:19:07','2023-09-01 12:19:07','',66,'https://test.solino.com.tr/?p=195',0,'revision','',0);
